mapper.select:
SELECT
	*
FROM BPP_CMMN_BOARD_MGM
WHERE
	BPP_CMMN_BOARD_FNTP = #{bppCmmnBoardFntp}
	AND USE_AT = #{useAt}

SELECT 
	*
FROM BPP_CMMN_BOARD_MGM
WHERE
	BPP_CMMN_BOARD_SEQ_NO = #{bppCmmnBoardSeqNo}
	AND USE_AT = #{useAt}

SELECT 
	COUNT(*)
FROM BPP_CMMN_BOARD_MGM
WHERE
	BPP_CMMN_BOARD_FNTP = #{bppCmmnBoardFntp}
	AND USE_AT = #{useAt}

SELECT 
	*
FROM BPP_CMMN_BOARD_MGM
WHERE
	BPP_CMMN_BOARD_SEQ_NO = #{bppCmmnBoardSeqNo}

		AND FIRST_WRTR_ID = #{loginId}

	AND USE_AT = #{useAt}

mapper.update:
UPDATE BPP_CMMN_BOARD_MGM SET BPP_CMMN_BOARD_ANSWER = #{bppCmmnBoardAnswer}, BOARD_ANSWER_COMPT_AT = #{boardAnswerComptAt},
USE_AT = #{useAt},
LAST_UPDT_DT = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'), LAST_UPDUSR_ID = #{loginId} WHERE BPP_CMMN_BOARD_SEQ_NO = #{bppCmmnBoardSeqNo}
